import { Paragraph } from "./Text";
import React from "react";
import media from "../media";
import styled from "styled-components";

const ButtonTemplate = styled.button`
	position: relative;
	cursor: pointer;
	appearance: none;
	border: none;
	/* border-radius: 3px; */
	padding: 13px 20px;
	font-size: 16px;
	line-height: 16px;
	font-weight: 600;
	font-family: "myriad-pro";
	width: 100%;

	background-color: #b02834;
	color: white;

	${({ theme }) =>
		({
			grey: `background-color: #E1E1E1; color: #1A1A1A;`,
		}[theme])}

	${media.minWidth("m")`
		width: auto;
	`}

	${media.minWidth("l")`
		${({ suspend }) => suspend && `margin-top: 40px`}
	`}

	${({ center }) => center && `display: block;margin: 0 auto;`}
`;

const External = styled.div`
	display: inline-block;
	background-image: url("${require("../assets/icons/external.svg").default}");
	background-size: cover;
	width: 11px;
	height: 11px;
	margin-right: 8px;
`;
const Chevron = styled.div`
	display: inline-block;
	background-image: url("${require("../assets/icons/chevron-down.svg")
		.default}");
	background-size: cover;
	width: 11px;
	height: 8px;
	margin-left: 8px;
`;

const DropdownLeaf = styled.ul`
	background-color: white;
	list-style-type: none;
	padding: 15px;
	display: ${({ active }) => (active ? "grid" : "none")};
	row-gap: 15px;
	box-shadow: 0px 0px 4px rgba(0, 0, 0, 0.25);

	li {
		border-bottom: 1px solid rgba(0, 0, 0, 0.1);
	}
`;

export default ({ dropdown, options, external, children, ...props }) => {
	const [active, setActive] = React.useState(false);

	const deactivate = () => {
		setActive(false);
		window.removeEventListener("click", deactivate);
	};

	const handleClick = () => {
		if (active) {
			setActive(false);
		} else {
			setActive(true);

			window.addEventListener("click", deactivate);
		}
	};

	React.useEffect(() => {
		return () => deactivate();
	});

	return (
		<React.Fragment>
			<ButtonTemplate {...props} onClick={handleClick}>
				{external && <External />}
				{children} {dropdown && <Chevron />}
			</ButtonTemplate>
			{dropdown && (
				<DropdownLeaf active={active}>
					{options.dropdown.map(({ value, href }) => (
						<Paragraph as="li">
							{href ? <a href={href}>{value}</a> : value}
						</Paragraph>
					))}
				</DropdownLeaf>
			)}
		</React.Fragment>
	);
};
