import media from "../media";
import styled from "styled-components";

const Outer = styled.section`
	${({ dark }) => dark && `background-color: #1a1a1a; color: white;`}
`;

const Inner = styled.section`
	height: 100%;
	padding: 50px 0;

	width: 600px;
	margin: 0 auto;

	${media.maxWidth("l")`
		/* max-width: calc(100% - 30px); */
		background-color: red;
	`}

	${media.minWidth("l")`
		max-width: auto;
		padding: 75px 0;
		width: 1125px;
	`}
`;

export default ({ children, dark, outerStyle, innerStyle }) => {
	return (
		<Outer dark={dark} style={outerStyle}>
			<Inner style={innerStyle}>{children}</Inner>
		</Outer>
	);
};
