import breakpoint from "styled-components-breakpoint";
import styled from "styled-components";

export default styled.section`
	display: grid;

	${breakpoint("lg")`
		grid-template-columns: repeat(80px, 12);
		column-gap: 15px;
		row-gap: 15px;
	`}
`;
