import media from "../media";
import styled from "styled-components";

export default styled.section`
	height: 100%;
	padding: 50px 0;

	width: 600px;
	max-width: calc(100% - 30px);
	margin: 0 auto;

	${({ dark }) => dark && `background-color: #1a1a1a; color: white;`}

	${media.minWidth("l")`
		padding: 75px 0;
		width: 1125px;
	`}
`;
