import breakpoint from "styled-components-breakpoint";
import styled from "styled-components";

export const Heading = styled.h1`
	font-family: "URW Antiqua";
	font-style: italic;
	font-weight: 800;
	font-size: 32px;
	line-height: 106.3%;

	margin: 0;

	${breakpoint("lg")`
        font-size: 42px;
    `}
`;

export const Paragraph = styled.p`
	font-family: "Myriad Pro";
	font-style: normal;
	font-weight: normal;
	font-size: 18px;
	line-height: 130.3%;

	margin: 0;
`;

export const Subheading = styled.h5`
	font-family: Myriad Pro;
	font-style: normal;
	font-weight: 600;
	font-size: 12px;
	line-height: 14px;

	color: #5f5f5f;

	margin: 0;
`;
