import DefaultStrapline from "../Strapline";
import breakpoint from "styled-components-breakpoint";
import styled from "styled-components";

export const Wrapper = styled.div`
	background-color: #1a1a1a;
	padding: 100px 0;
`;

export const Strapline = styled(DefaultStrapline)`
	text-align: left;
	color: white;

	grid-column: 1 / -1;

	${breakpoint("lg")`
        grid-column: 1 / 7;
    `}
`;
