import media from "../media";
import styled from "styled-components";

const Outer = styled.section`
	${media.maxWidth("l")`
		${({ explode }) => explode && `padding: 25px 0;`}
	`}
	${media.minWidth("l")`
		${({ explode }) => explode && `padding: 75px 0;`}
	`}
	${({ dark }) => dark && `background-color: #1a1a1a; color: white;`}
`;

const Inner = styled.div`
	height: 100%;
	padding: 50px 0;

	width: 600px;
	margin: 0 auto;

	${media.maxWidth("l")`
		max-width: calc(100% - 30px);
	`}

	${media.minWidth("l")`
		padding: 75px 15px;
		width: 1125px;
	`}

	${({ deflate }) => deflate && `padding: 0 !important;`}
`;

export default ({
	children,
	dark,
	deflate,
	explode,
	outerStyle,
	innerStyle,
}) => {
	return (
		<Outer explode={explode} dark={dark} style={outerStyle}>
			<Inner style={innerStyle} deflate={deflate}>
				{children}
			</Inner>
		</Outer>
	);
};
