import { Heading } from "../Text";
import breakpoint from "styled-components-breakpoint";
import styled from "styled-components";

export const Panel = styled.div`
	grid-column: span 3;

	${breakpoint("lg")`
        grid-column: span 4; 
    `}
`;

export const Title = styled.h4`
	font-family: myriad-pro;
	font-style: normal;
	font-weight: 300;

	${breakpoint("lg")`
        font-size: 16px;
        line-height: 19px;
    `}
`;
