import breakpoint from "styled-components-breakpoint";
import styled from "styled-components";

export default styled.hr`
	height: 2px;
	border: none;

	background-color: rgba(26, 26, 26, 0.25);
	margin: -50px 0 150px;

	grid-column: 1 / -1;

	${breakpoint("lg")`
        grid-column: 2 / -2;
    `}
`;
