import styled from "styled-components";

const burgerWidth = 24,
	burgerHeight = 24,
	barHeight = 2,
	barDefaults = `
    transform-origin: center;
    background-color: #000000;
    will-change: opacity, transform;
    transition: all 0.23s;
    width: ${burgerWidth}px;
    height: ${barHeight}px;
    position: absolute;
`;

export const Bars = {
	Top: styled.div`
		${barDefaults}
		top: ${burgerHeight / 2 - 5 - barHeight / 2}px;
	`,
	Middle: styled.div`
		${barDefaults}
		top: ${burgerHeight / 2 - barHeight / 2}px;
	`,
	Bottom: styled.div`
		${barDefaults}
		top: ${burgerHeight / 2 + 5 - barHeight / 2}px;
	`,
};

export const Wrapper = styled.div`
	grid-column: -2 / -1;
	width: 24px;
	height: 24px;
	justify-self: center;
	position: relative;
	transform-origin: center;

	${({ active }) =>
		active &&
		`
        ${Bars.Top} {
            transform: translateY(5px) rotate(45deg);
        }
        ${Bars.Middle} { opacity: 0; }
        ${Bars.Bottom} {
            transform: translateY(-5px) rotate(-45deg);
        }
    `}
`;
