import ActivityIndicator from "../../components/ActivityIndicator";
import Button from "../../components/Button";
import Feature from "../../components/Feature";
import Lattice from "../../components/Lattice";
import { Paragraph } from "../../components/Text";
import React from "react";
import Section from "../../components/Section";
import Strapline from "../../components/Strapline";
import ellipsize from "ellipsize";
import getEpisodes from "../../api/getEpisodes";

export default () => {
	const [[firstEpisode, ...episodes], setEpisodes] = React.useState([]);

	React.useEffect(() => {
		(async function () {
			let episodes = await getEpisodes();

			console.log({ episodes });

			setEpisodes(
				episodes.sort(
					(a, b) => new Date(b.pubDate) - new Date(a.pubDate)
				)
			);
		})();
	}, []);

	const ytidToImage = (ytid) =>
		`https://img.youtube.com/vi/${ytid}/hqdefault.jpg`;

	console.log({ firstEpisode });

	return (
		<React.Fragment>
			<ActivityIndicator fullScreen active={!episodes.length}>
				Loading podcasts...
			</ActivityIndicator>
			{episodes.length && (
				<React.Fragment>
					<Section explode>
						<Feature invert>
							<Feature.Title>{firstEpisode.title}</Feature.Title>
							<Feature.Image
								src={ytidToImage(firstEpisode.ytid)}
							/>
							<Feature.Body>
								<Paragraph>
									{ellipsize(firstEpisode.desc, 150)}
								</Paragraph>
							</Feature.Body>
						</Feature>
					</Section>
					<Section deflate>
						<Strapline>
							On the Christian Heritage London podcast we meet
							Christian leaders who serve the church in the
							purpose, perspective and power of the gospel.
						</Strapline>
						<Button
							dropdown
							center
							options={{
								items: [
									{
										value: "Apple Podcasts",
										href: "https://podcasts.apple.com/gb/podcast/christian-heritage-london-podcast/id1232042916",
									},
									{
										value: "Spotify",
										href: "https://podcasts.apple.com/gb/podcast/christian-heritage-london-podcast/id1232042916",
									},
								],
							}}
						>
							Subscribe
						</Button>
					</Section>
					<Section explode>
						<Lattice
							panels={episodes.map(({ title, ytid }) => ({
								title,
								icon: "video",
								image: ytidToImage(ytid),
							}))}
						/>
					</Section>
				</React.Fragment>
			)}
		</React.Fragment>
	);
};
