import styled, { keyframes } from "styled-components";

const fadeOut = keyframes`
	0% {
		opacity: 1;
	}
	77% {
		opacity: 1;
	}
	to {
		opacity: 0;
	}
`;

export const Wrapper = styled.div`
	display: flex;
	flex-direction: column;
	justify-content: center;
	align-items: center;

	${({ active }) => !active && `animation: 1s ${fadeOut} forwards;`}

	${({ fullScreen }) =>
		fullScreen
			? `
		left: 0; top: 0;
		width: 100vw;
		height: 100vh;
		position: fixed;
		z-index: 99999;
		background-color: white;
		`
			: `
		margin: 30px 0;
		width: 100%;
		grid-column: 1 / -1;
		height: 100px;
	`}
`;
