import Button from "../Button";
import { Input } from "../Form";
import styled from "styled-components";

export const Wrapper = styled.div`
	height: 40px;
	display: flex;
	align-items: top;
	position: relative;

	${Input} {
		flex: 1;
	}

	${Input},
	${Button} {
		color: white;
		height: 100% !important;
	}
`;
