import { Heading } from "../Text";
import breakpoint from "styled-components-breakpoint";
import styled from "styled-components";

export const LatticePanel = styled.div`
	grid-column: span 3;

	${Heading} {
		font-family: myriad-pro;
		font-style: normal;
		font-weight: 300;
	}

	${breakpoint("lg")`
        grid-column: span 4;

        ${Heading} {
            font-size: 16px;
		    line-height: 19px;
        }
    `}
`;
