import { Paragraph, Subheading } from "../Text";

import breakpoint from "styled-components-breakpoint";
import styled from "styled-components";

export const Panel = styled.div`
	grid-column: span 3;

	${breakpoint("lg")`
        grid-column: span 4; 
    `}
`;

export const Image = styled.div`
	padding-bottom: 60%;
	background-size: cover;
	background-image: url("${({ src }) => src}");
`;

export const Title = styled.h3(Paragraph)`
	font-size: 16px;
	line-height: 19px;

	${breakpoint("lg")`
        font-size: 20px;
        line-height: 24px;
    `}
`;

export const Subtitle = styled.h6(Subheading)`
    font-size: 12px;
    line-height: 14px;
`;
