import breakpoint from "styled-components-breakpoint";
import styled from "styled-components";

export default styled.section`
	margin-bottom: 75px;

	${({ top }) => top && `margin-top: 50px`}

	${breakpoint("lg")`
		${({ top }) => top && `margin-top: 150px`}
		margin-bottom: 150px;
	`}
`;
