import Button from "../Button";
import Grid from "../Grid";
import { Heading } from "../Text";
import breakpoint from "styled-components-breakpoint";
import styled from "styled-components";

const lgCaption = inverted`
    grid-column: ${!inverted ? `1 / 5` : `7 / -2`};
`;

export const FeatureGrid = styled(Grid)`
	grid-template-columns: 1fr;
	grid-template-areas:
		"heading"
		"image"
		"paragraph"
		"button";

	${breakpoint("lg")`
        grid-template-columns: 2fr 1fr 3fr;
        grid-template-areas: 
            "heading . image"
            "paragraph . image"
            "button . image";
    `}
`;

export const FeatureHeading = styled(Heading)`
	grid-area: heading;
`;

export const FeatureImage = styled.div`
	grid-area: image;

	width: calc(100% + 30px);
	margin-left: -15px;
`;

export const FeatureParagraph = styled(Paragraph)`
	grid-area: paragraph;
`;

export const FeatureButton = styled(Button)`
	grid-area: button;

	${breakpoint("lg")`
        margin-top: 40px;
    `}
`;
