import { Heading, Paragraph } from "../Text";

import Button from "../Button";
import Grid from "../Grid";
import breakpoint from "styled-components-breakpoint";
import styled from "styled-components";

export const FeatureGrid = styled(Grid)`
	grid-template-columns: 1fr;
	grid-template-areas:
		"heading"
		"image"
		"paragraph"
		"button";

	${breakpoint("lg")`
        grid-template-rows: auto auto auto auto auto;
        ${({ inverted }) =>
			!inverted
				? `grid-template-columns: 2fr 1fr 3fr;
        grid-template-areas: 
            ". . image"
            "heading . image"
            "paragraph . image"
            "button . image"
            ". . image";`
				: `grid-template-columns: 6fr 1fr 4fr 1fr;
        grid-template-areas: 
            "image . . ."
            "image . heading ."
            "image . paragraph ."
            "image . button ."
            "image . . .";  `}
    `}
`;

export const FeatureHeading = styled(Heading)`
	grid-area: heading;
`;

export const FeatureImage = styled.div`
	grid-area: image;

	background-image: url("${({ src }) => src}");
	background-size: cover;
	border-radius: 3px;

	${breakpoint("xs", "lg")`
        width: calc(100% + 30px);
        margin-left: -15px;
        height: 20vw;
    `}

	${breakpoint("lg")`
        height: 350px;
    `}
`;

export const FeatureParagraph = styled(Paragraph)`
	grid-area: paragraph;
`;

export const ButtonWrapper = styled.div`
	grid-area: button;

	${breakpoint("lg")`
        margin-top: 25px;
    `}
`;
