import Button from "../Button";
import styled from "styled-components";

export const Wrapper = styled.div`
	height: 40px;
	display: flex;
	align-items: top;
	position: relative;

	${Button} {
		flex: 1;
		line-height: 0px !important;
	}

	input,
	label {
		color: white;
		height: 100% !important;
	}
`;
