import Grid from "../../components/Grid";
import breakpoint from "styled-components-breakpoint";
import styled from "styled-components";

export const Banner = styled.div`
	position: relative;
	transform: translateX(50%);
	left: -50%;
	width: 100vw;

	background-repeat: no-repeat;
	background-size: 100% auto;

	${breakpoint("xs", "lg")`
		background-image: url("${require("../../assets/urban-mobile.jpg")}");
		padding-top: 368px;
	`}

	${breakpoint("lg")`
		background-image: url("${require("../../assets/urban-desktop.jpg")}");
		min-height: 400px;
        background-size: 50% auto;
    `}
`;

export const Names = styled.div`
	background-image: url("${require("../../assets/names.svg").default}");
	background-size: contain;
	background-repeat: no-repeat;
	grid-column: 1 / -1;
	padding-bottom: calc(25200% / 354);

	${breakpoint("lg")`
		padding-bottom: calc(14100% / 252);
		transform: translateY(8%);
		grid-column: 6 / 12;
	`}
`;

export const Text = styled.div`
	grid-column: 1 / -1;

	${breakpoint("lg")`
		grid-column: 8 / 12;
	`}
`;
