import media from "../media";
import styled from "styled-components";

export default styled.section`
	height: 100%;
	padding: 50px 0;

	${({ dark }) => dark && `background-color: #1a1a1a; color: white;`}

	${breakpoint("lg")`
		padding: 75px 0;
	`}
`;
