import { Caption } from "./Text";
import media from "../media";
import styled from "styled-components";

const Form = styled.form`
	grid-column: 1 / -1;

	// Grid stuff...
	display: grid;

	column-gap: 15px;
	row-gap: 15px;
	grid-template-columns: repeat(6, 1fr);
	align-items: top;

	${media.minWidth("l")`
		grid-template-columns: repeat(12, 1fr);
	`}
`;

Form.Group = styled.section`
	position: relative;

	grid-column: 1 / -1;
	margin-bottom: 15px;

	${media.minWidth("l")`
		grid-column: 1 / 5;
		margin-bottom: 30px;
	`}
`;

Form.Label = styled(Caption)``;

const standardFieldStyles = `
	display: block;
	box-sizing: border-box;
	border: 2px solid #e1e1e1; //#b02834;
	font-family: "myriad-pro";
	font-weight: 300;

	will-change: border-color;
	transition: border-color 0.23s;

	${media.maxWidth("l")`
		grid-column: 1 / -1;
	`}

	${media.minWidth("l")`
		grid-column: 1 / 5;
	`}

	&:focus {
		border-color: #b02834;
	}
`;

Form.Input = styled.input`
	height: 40px;

	${standardFieldStyles}
`;

Form.Textarea = styled.textarea`
	min-height: 120px;
	max-height: 300px;
	resize: vertical;

	${standardFieldStyles}
`;

export default Form;
