import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCaretDown } from "@fortawesome/free-solid-svg-icons";
import media from "../media";
import styled from "styled-components";

const ButtonTemplate = styled.button`
	appearance: none;
	border: none;
	border-radius: 3px;
	padding: 13px 20px;
	font-size: 16px;
	line-height: 16px;
	font-weight: 500;
	font-family: "myriad-pro";
	width: 100%;

	${({ theme = "red" }) =>
		({
			red: `background-color: #B02834; color: white;`,
			grey: `background-color: #E1E1E1; color: #1A1A1A;`,
		}[theme])}

	${media.minWidth("m")`
		width: auto;
	`}

	${media.minWidth("l")`
		${({ suspend }) => suspend && `margin-top: 40px`}
	`}
`;

export default ({ dropdown, children, ...props }) => {
	return (
		<ButtonTemplate {...props}>
			{children} {dropdown && <FontAwesomeIcon icon={faCaretDown} />}
		</ButtonTemplate>
	);
};
