import { Background, Player } from "./styles";

import React from "react";
import Section from "../Section";

export default ({ src, ...props }) => {
	return (
		<Background active={!!src}>
			<Section style={{ height: "100%" }}>
				<Player src={src} {...props} />
			</Section>
		</Background>
	);
};
